import './Dashboard.css';
import { useState, useMemo, useEffect} from 'react';
import BarChart from './BarChart';


const baseurl = "http://localhost:9000/api/data";





export default function EYvsP()
{
    const[countryList, setCountryList] = useState([]);
    const[country, setCountry] = useState('Australia');

    const handlechange = (selcountry) =>{
        setCountry(selcountry.target.value);
        // console.log(selcountry.target.value);
    }
 

    useMemo(()=>{fetch(baseurl+'/countrynames')
    .then(
        (response)=> response.json()
    )
    .then(
        (json) => {

            var data = []
          
            for(let i = 0; i < json.length; i++)
            {
                data.push({id: i,value:json[i], label:json[i]});
                   
            } 
            setCountryList(data);
            
            // console.log(countryList);
        }
    )
    .catch(
        (error)=>{console.log("err")}
    );},[]);
 

    const[chartdata , setChartData] = useState({
        label : ['a','b','c','d'],
        datasets: [
            {
                title :"Barchart",
                data :  [1,2,3,4],
                backgroundColor : ['#ff0000'],
                borderWidth: 1,
            }
        ]
        });
    
    useEffect(()=>{
        fetch(`${baseurl}/country/${pestle}`)
        .then((response)=> response.json()
        ).then((json)=>{
                
                let dataset = new Map();
                for(let i = 0 ; i < json.length ; i++)
                {
                    if(json[i].pestle !== "" || json[i].pestle !== " ")
                    {
                    if(dataset.has(json[i].pestle))
                    {
                        let cnt = dataset.get(json[i].pestle) + 1;
                        dataset.set(json[i].pestle, cnt);
                    }
                    else{
                        dataset.set(json[i].pestle,1);
                    }
                    }
                }

                let labels = [];
                let values = [];
                
                dataset.forEach((k,v)=>{
                    labels.push(v);
                    values.push(k);
                });
                console.log(labels);
                var chartdata = {
                    labels : labels,
                    datasets: [
                        {
                            label :"No. of Published articles",
                            data :  values,
                            backgroundColor : ['#FF920E'],
                            borderWidth: 1,
                        }
                    ]
                    };
                
                    setChartData(chartdata);
                
        
        })
        .catch((error)=>{console.log(error)});
    },[country]);
    

    



    return (
        <div className="chart1">
              <span className='filter'>Country : </span> 
              <select className='select-box' value={country}  onChange={handlechange}>{countryList.map((option) => (<option value={option.value}>{option.label}</option>))}</select>
                <BarChart chartData= {chartdata} title="Endyear vs Pestle" desc="Endyear vs Pestle"/>
        </div>
    );
}
