import './Dashboard.css';

import axios from 'axios';
import { useState, useEffect} from 'react';
const baseurl = "";


function selectData(countryList)
{
    var text = ""
    for(let country in countryList)
    {
        text += `<option value ${country}>${country}</option>\n`;
    }
        return text;
}


export default function CvsPonEY()
{

    var countryList = [];
    useEffect(()=>
    {
        axios.get(`${baseurl}/countrynames/`)
        .then(
            (response)=>{
                countryList.fill(response.data);
            }
        )
        .catch(
            (error)=>{console.log(error)}
        );
    });

    const[chartdata , setChartData] = useState(null);
    const[country, setcountry] = useState('Australia');
    useEffect(()=>{
        axios.get(`${baseurl}/country/${country}`)
        .then((response)=>{
            setChartData(response.data);
        })
        .catch((error)=>{console.log(error)});
    });
    

    // const chartdata = {
    //     labels: ['Red', 'Orange', 'Blue'],
    //     datasets: [
    //         {
    //           label: 'Popularity of colours',
    //           data: [55, 23, 96],
    //           backgroundColor: [
    //             '#ff00ff',
    //             '#ff0000',
    //             '#0000ff'
    //             ],
    //             borderWidth: 1,
    //                 }
    //             ]
    //     };




    return (
        <div className="chart1">
              <select className='select-box'>{countryList && selectData(countryList)}</select>
            {/* <Barchart chatData= {chartdata} title="Country vs Published" desc="Country vs Publised"/> */}
        </div>
    );
}
