import './Dashboard.css';
import { useState, useMemo} from 'react';



const baseurl = "http://localhost:9000/api/data";





export default function CvsPonEY()
{
    const[countryList, setCountryList] = useState([]);
    const[country, setCountry] = useState('Australia');

    const handlechange = (selcountry) =>{
        setCountry(selcountry.value);
    }
 

    useMemo(()=>{fetch(baseurl+'/countrynames')
    .then(
        (response)=> response.json()
    )
    .then(
        (json) => {

            var data = []
          
            for(let i = 0; i < json.length; i++)
            {
                data.push({id: i,value:json[i], label:json[i]});
                   
            } 
            setCountryList(data);
            
            // console.log(countryList);
        }
    )
    .catch(
        (error)=>{console.log("err")}
    );},[]);
 

    // const[chartdata , setChartData] = useState(null);
    // const[country, setcountry] = useState('Australia');
    // useEffect(()=>{
    //     axios.get(`${baseurl}/country/${country}`)
    //     .then((response)=>{
    //         setChartData(response.data);
    //     })
    //     .catch((error)=>{console.log(error)});
    // });
    

    // const chartdata = {
    //     labels: ['Red', 'Orange', 'Blue'],
    //     datasets: [
    //         {
    //           label: 'Popularity of colours',
    //           data: [55, 23, 96],
    //           backgroundColor: [
    //             '#ff00ff',
    //             '#ff0000',
    //             '#0000ff'
    //             ],
    //             borderWidth: 1,
    //                 }
    //             ]
    //     };




    return (
        <div className="chart1">
              { countryList && <select value={country} onChange={handlechange}>{countryList.map((option) => (<option value={option.value}>{option.label}</option>))}</select>}
            {/* <Barchart chatData= {chartdata} title="Country vs Published" desc="Country vs Publised"/> */}
        </div>
    );
}
