import { useState, useMemo} from 'react';
import Select from 'react-select';


const baseurl = "http://localhost:9000/api/data";





export default function CvsPonEY()
{
    const[countryList, setCountryList] = useState(null);
    const[selectedOption, setSelectedOption] = useState('Australia');


    const handleChange = (seloption)=>{
        setSelectedOption(seloption);
    }

    useMemo(()=>{fetch(baseurl+'/countrynames')
    .then(
        (response)=> response.json()
    )
    .then(
        (json) => {

            
            setCountryList(json);
            
            // console.log(countryList);
        }
    )
    .catch(
        (error)=>{console.log("err")}
    );},[]);
 

    // const[chartdata , setChartData] = useState(null);
